import React from 'react'
import {connect} from 'react-redux'
import * as R from 'ramda'
import {Link} from 'react-router-dom'

import {
  removeClotheFromBasket,
  basketCheckout,
  cleanBasket
} from 'actions'

import {
  getBasketClothesWithCount,
  getTotalBasketPrice
} from 'selectors'

const Basket = ({
  clothes,
  totalPrice,
  removeClotheFromBasket,
  basketCheckout,
  cleanBasket
}) => {
  const isBasketEmpty = R.isEmpty(clothes)
  const renderContent = () => {
    return (
      <div>
        {isBasketEmpty && <div>Ваша корзина пуста</div>}

        <div className='table-responsive'>
          <table className='table-bordered table-striped table-condensed cf'>
            <tbody>
            {clothes.map((clothe, index) => (
              <tr
                key={index}
                className='item-checout'
              >
                <td className='first-column-checkout'>
                  <img
                    className='img-thumbnail'
                    src={clothe.image}
                    alt={clothe.name}
                  />
                </td>
                <td>{clothe.name}</td>
                <td>₴{clothe.price}</td>
                <td>{clothe.count}</td>
                <td>
                  <span
                    onClick={() => removeClotheFromBasket(clothe.id)}
                    className='delete-cart'
                  ></span>
                </td>
              </tr>
            ))}
            </tbody>
          </table>
        </div>
        {
          R.not(isBasketEmpty) &&
          <div className='row'>
            <div className='pull-right total-user-checkout'>
              <b>До оплати:</b>
              ₴{totalPrice}
            </div>
          </div>
        }
      </div>
    )
  }
  const renderSidebar = () => (
    <div>
      <Link
        className='btn btn-info'
        to='/'
      >
        <span className='glyphicon glyphicon-info-sign'></span>
        <span>Продовжити покупки</span>
      </Link>
      {
        R.not(isBasketEmpty) &&
        <div>
          <button
            onClick={cleanBasket}
            className='btn btn-danger'
          >
            <span className='glyphicon glyphicon-trash'></span>
            Очистити корзину
          </button>
          <button
            className='btn btn-success'
            onClick={() => basketCheckout(clothes)}
          >
            <span className='glyphicon glyphicon-envelope'></span>
            Чек
          </button>
        </div>
      }
    </div>
  )

  return (
    <div className='view-container'>
      <div className='container'>
        <div className='row'>
          <div className='col-md-9'>
            {renderContent()}
          </div>
          <div className='col-md-3'>
            {renderSidebar()}
          </div>

        </div>
      </div>
    </div>
  )
}

const mapStateToProps = state => {
  return {
    clothes: getBasketClothesWithCount(state),
    totalPrice: getTotalBasketPrice(state)
  }
}

const mapDispatchToProps = {
  removeClotheFromBasket,
  cleanBasket,
  basketCheckout
}

export default connect(mapStateToProps, mapDispatchToProps)(Basket)