import React, {Component} from 'react'
import {connect} from 'react-redux'
import * as R from 'ramda'
import {Link} from 'react-router-dom'

import {fetchClotheById, addClotheToBasket} from 'actions'
import {getClotheById} from 'selectors'
import BasketCart from 'components/basketCart'

class Clothe extends Component {
  componentDidMount() {
    const clotheId = this.props.match.params.id
    this.props.fetchClotheById(clotheId)
  }

  renderField() {
    const {clothe} = this.props
    const columnField = R.compose(
      R.toPairs,
      R.pick([
        'Артикул',
        'Бренд',
        'Категорія',
        'Сезон',
        'Виробник',
        'Колір',
      ])
    )(clothe)

    console.log('columns', columnField)

    return columnField.map(([key, value]) => (
      <div className='column' key={key}>
        <div className='ab-details-title'>
          <p>{key}</p>
        </div>
        <div className='ab-details-info'>
          {value}
        </div>
      </div>
    ))
  }

  renderContent() {
    const {clothe} = this.props

    return (
      <div className='thumbnail'>
        <div className='row'>
          <div className='col-md-6'>
            <img
              className='img-thumbnail'
              src={clothe.image}
              alt={clothe.name}
            />
          </div>
          <div className='col-md-6'>
            {this.renderField()}
          </div>
        </div>
        <div className='caption-full'>
          <h4 className='pull-right'>₴{clothe.price}</h4>
          <h4>{clothe.name}</h4>
          <p>{clothe.description}</p>
        </div>
      </div>
    )
  }

  renderSidebar() {
    const {clothe, addClotheToBasket} = this.props

    return (
      <div>
        <p className='lead'>Швидка покупка</p>
        <BasketCart/>
        <div className='form-group'>
          <h1>{clothe.name}</h1>
          <h2>₴{clothe.price}</h2>
        </div>
        <Link to='/' className='btn btn-info btn-block'>
          Назад до покупок
        </Link>
        <button
          type='button'
          className='btn btn-success btn-block'
          onClick={() => addClotheToBasket(clothe.id)}
        >
          Додати в кошик
        </button>
      </div>
    )
  }

  render() {
    return (
      <div className='view-container'>
        <div className='container'>
          <div className='row'>
            <div className='col-md-9'>
              {this.props.clothe && this.renderContent()}
            </div>
            <div className='col-md-3'>
              {this.props.clothe && this.renderSidebar()}
            </div>
          </div>
        </div>
      </div>
    )
  }
}

const mapStateToProps = state => {
  return {
    clothe: getClotheById(state, state.clothePage.id)
  }
}

const mapDispatchToProps = {
  fetchClotheById,
  addClotheToBasket
}

export default connect(mapStateToProps, mapDispatchToProps)(Clothe)