import React, {Component} from 'react'
import {connect} from 'react-redux'
import {Link} from 'react-router-dom'
import * as R from 'ramda'

import {
  fetchClothes,
  loadMoreClothes,
  addClotheToBasket,
  fetchCategories
} from 'actions'
import {getClothes} from 'selectors'
import Layout from 'containers/layout'

class Clothes extends Component {
  componentDidMount() {
    this.props.fetchClothes()
    this.props.fetchCategories()
  }

  renderClothe(clothe, index) {
    const {addClotheToBasket} = this.props
    const shortDescription = `${R.take(60, clothe.description)}...`

    return (
      <div className='col-sm-4 col-lg-4 col-md-4 book-list' key={index}>
        <div className='thumbnail'>
          <img
            className='img-thumbnail'
            src={clothe.image}
            alt={clothe.name}
          />
          <div className='caption'>
            <h4 className='pull-right'>₴{clothe.price}</h4>
            <h4>
              <Link to={`/clothes/${clothe.id}`}>
                {clothe.name}
              </Link>
            </h4>
            <p>{shortDescription}</p>
            <p className='itemButton'>
              <button
                className='btn btn-primary'
                onClick={() => addClotheToBasket(clothe.id)}
              >
                Купити зараз
              </button>
              <Link
                to={`/clothes/${clothe.id}`}
                className='btn btn-default'
              >
                Інформація
              </Link>
            </p>
          </div>
        </div>
      </div>
    )
  }

  render() {
    const {clothes, loadMoreClothes} = this.props
    return (
      <Layout>
        <div className='books row'>
          {clothes.map((clothe, index) => this.renderClothe(clothe, index))}
        </div>
        <div className='row'>
          <div className='col-md-12'>
            <button
              onClick={loadMoreClothes}
              className='pull-right btn btn-primary'
            >
              Загрузити ще
            </button>
          </div>
        </div>
      </Layout>
    )
  }
}

const mapStateToProps = (state, ownProps) => ({
  clothes: getClothes(state, ownProps)
})

const mapDispatchToProps = {
  fetchClothes,
  loadMoreClothes,
  addClotheToBasket,
  fetchCategories
}

export default connect(mapStateToProps, mapDispatchToProps)(Clothes)
